package conditionstatement;



public class Forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 0; i <= 3; i++) {
			System.out.println("Enter the sum" + i);
		}

		for (float j = 2.2f; j <= 5.4f; j++) {
			System.out.println(j);
		}
		for (char r ='A'; r <= 'G'; r++) {
			System.out.print(r);
		}
		System.out.println();
		
		int i=2;
		while(i<=6) {
			System.out.print(i);
			i++;
		}
		System.out.println();
		int t=0;
		do {
			System.out.println("Hello");
			t++;
		}while(t<=5);
		
		
	
	}

}
